# 取出所有用户
user_ids = list(user_to_group.keys())

# ====== 2. 构造用户embedding ======
# 这里为了演示，用 one-hot + 交互的 bundle 做平均，得到用户embedding
bundle_ids = sorted(df["bundleId"].unique())
bundle2idx = {b: i for i, b in enumerate(bundle_ids)}

embeddings = []
for u in user_ids:
    interacted_bundles = df[df["userId"] == u]["bundleId"].tolist()
    if len(interacted_bundles) == 0:
        vec = np.zeros(len(bundle_ids))
    else:
        vec = np.zeros(len(bundle_ids))
        for b in interacted_bundles:
            vec[bundle2idx[b]] = 1
        vec = vec / np.linalg.norm(vec)  # 归一化
    embeddings.append(vec)

embeddings = np.array(embeddings)

# ====== 3. t-SNE 降维 ======
tsne = TSNE(n_components=2, random_state=42, init="pca", learning_rate="auto")
embeddings_2d = tsne.fit_transform(embeddings)

# ====== 4. 可视化 ======
plt.figure(figsize=(10, 8))
groups = [user_to_group[u] for u in user_ids]

scatter = plt.scatter(
    embeddings_2d[:, 0],
    embeddings_2d[:, 1],
    c=groups,
    cmap="tab20",
    s=40,
    alpha=0.8
)

plt.colorbar(scatter, label="Group ID")
plt.title("t-SNE visualization of users (colored by group)")
plt.xlabel("Dimension 1")
plt.ylabel("Dimension 2")
plt.show()
